pub fn sort<F>(&self, column: &str, mut sort_func: F) -> DataFrame
where
    F: FnMut(&DataValue, &DataValue) -> std::cmp::Ordering,
{
    let col_idx = match self.columns.iter().position(|c| c == column) {
        Some(idx) => idx,
        None => return DataFrame::from_rows(self.columns.clone(), vec![]), // Return empty if column not found
    };

    let mut sorted_rows = self.data.clone();
    sorted_rows.sort_by(|a, b| {
        let va = a.get(col_idx).unwrap_or(&DataValue::Null);
        let vb = b.get(col_idx).unwrap_or(&DataValue::Null);
        sort_func(va, vb)
    });

    DataFrame::from_rows(self.columns.clone(), sorted_rows)
}
